      todos: [...this.state.todos, res.data]
    });
  }

  render() {
    return ( <
      div className = "App" >
      <
      div className = "container" >
      <
      Header / >
      <
      Addtodo Addtodo = {
        this.Addtodo
      }
      / > <
      Todo todos = {
        this.state.todos
      }
      markComplete = {
        this.markComplete
      }
      deleteTodo = {
        this.deleteTodo
      }
      / > < /div > < /div >
    );
  }
}

export default App;
