import React, {
  Component
} from 'react';

class Addtodo extends Component {
  state = {
    title: ''
  }

  onSubmit = (e) => {
    e.preventDefault();
    this.props.Addtodo(this.state.title);
    this.setState({
      title: ""
    });
  }

  onChange = (e) => this.setState({
    [e.target.name]: e.target.value
  });

  render() {
    return ( <
      form onSubmit = {
        this.onSubmit
      }
      style = {
        {
          display: 'flex'
        }
      } >
      <
      input type = "text"
      name = "title"
      onChange = {
        this.onChange
      }
      value = {
        this.state.title
      }
      placeholder = "Add Stuff You Need To Do Fool!"
      style = {
        {
          flex: "10",
          padding: "5px"
        }
      }
      />

      <
      input type = "Submit"
      name = "Submit"
      value = "Submit"
      className = "btn"
      style = {
        {
          flex: "1"
        }
      }
      /> < /form >
    );
  }

}

export default Addtodo;
