import React, {
  Component
} from 'react';
import Todo from './components/Todo';
import Header from './components/Header';
import './App.css';

class App extends Component {
  state = {
    todos: [{
      id: 1,
      title: 'take-out the trash',
      completed: false
    }, {
      id: 2,
      title: 'studying more OOP',
      completed: false
    }, {
      id: 3,
      title: 'Work-out',
      completed: false
    }]
  }

  //toggle complete
  markComplete = (id) => {
    this.setState({
      todos: this.state.todos.map(todo => {
        if (todo.id === id) {
          todo.completed = !todo.completed
        }
        return todo;
      })
    });
  }

  // Delete Todo
  deleteTodo = (id) => {
    this.setState({
      todos: [...this.state.todos.filter(todo => todo.id !== id)]
    });
  }

  // Add Todo
  addTodo = (title) => {
    const newTodo = {
      id: 4,
      title,
      completed: false
    }
    this.setState({
      todos: {
        todos: [...this.state.todos,
          newTodo
        ]
      }
    });
  }

  render() {
    return ( <
      div className = "App" >
      <
      AddTodo addTodo = {
        this.addTodo
      }
      /> <
      Header / > <
      Todo todos = {
        this.state.todos
      }
      markComplete = {
        this.markComplete
      }
      deleteTodo = {
        this.deleteTodo
      }
      / > < /div >
    );
  }
}

export default App;
